    //                     <p>{user.phone}</p>
    //                     <p>{user.email}</p>
    //                 </div>
    //             )
    //         }
    //     });

    //     return userContact;
    // }

    return (
        users.map(user => {
            {user.id === userId} {
                <div key={user.id}>
                    <p>{user.phone}</p>
                    <p>{user.email}</p>
                </div>
            }
        })
    )
}

export default UserData;
