import { handleFetch } from '../../utils';
import { useEffect, useState } from 'react';
import { Link, Outlet } from 'react-router-dom';

function UserList () {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const getUsers = async () => {
            setUsers(await handleFetch(`${process.env.REACT_APP_URL}users`));
        }

        getUsers();
    }, []);

    const findingUser = (id) => {

        // const findUser = users.map(user => {
        //     if (user.id === id) {
        //         return (
        //             <div key={user.id}>
        //                 <p>{user.email}</p>
        //             </div>
        //         )
        //     }
        // });

        // return findUser;
    }

    return (
        <div className='content'>
        <Outlet/>
            {users.length ? (
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Username</th>
                            <th>Contact Information</th>
                        </tr>
                    </thead>
                    {users.map(user => {
                        return (
                            <tbody key={user.id}>
                                <tr>
                                    <td>{user.name}</td>
                                    <td>{user.username}</td>
                                    <td><Link to={`contact/${user.id}`}>Contact</Link></td>
                                </tr>    
                            </tbody>   
                        )
                    })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default UserList;
