import { handleFetch } from "../../utils";
import { useEffect, useState } from 'react';
// import AddPost from '../AddPost/AddPost';
import { useForm } from "react-hook-form";

function PostsList () {
    const [posts, setPosts] = useState([]);
    // const { register, handleSubmit } = useForm();

    // const onSubmit = data => {
    //     const updatedPosts = posts.push(data);

    //     setPosts(updatedPosts);
    // }

    useEffect(() => {
        const getPosts = async () => {
            const allPosts = await handleFetch`${process.env.REACT_APP_URL}posts`;
            const slicedPosts = allPosts.slice(0, 8);
            setPosts(slicedPosts);
            // setPosts(await handleFetch(`${process.env.REACT_APP_URL}posts`));
        }

        getPosts();
    }, []);

    return (
        <div className="content">
            <form onSubmit={handleSubmit(onSubmit)}>
                <input {...register("title", {required: true, maxLength: 50})}/>
                <input {...register("body", {required: true, maxLength: 300})}/>
                <input type="submit" />
            </form>
            {posts.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Body</th>
                        </tr>
                    </thead>
                {posts.map((post) => {
                    return (
                        <tbody key={post.id}>
                            <tr>
                                <td>{post.id}</td>
                                <td>{post.title}</td>
                                <td>{post.body}</td>
                            </tr>
                        </tbody>
                    )
                })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default PostsList;
