import { handleFetch } from '../../utils';
import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const UserData = () => {
    const [users, setUsers] = useState([]);
    const {id} = useParams();
    const userId = +id;
    const navigate = useNavigate();

    useEffect(() => {
        const getUsers = async () => {
            setUsers(await handleFetch(`${process.env.REACT_APP_URL}users`));
        }

        getUsers();
    }, []);

    const handlePreviousRoute = () => {
        navigate('/users');
    }

    return (
        <div className='user-page'>
            <div className='previous-page-wrapper' onClick={() => handlePreviousRoute()}>
                <span className='previous-page-img'/>
                <p>Go Back</p>
            </div>
            {users.map((user) => {
                return (
                    <div key={user.id}>
                        {user.id === userId ? (
                            <ul className='user-info'>
                                <li>
                                    <span className='mobile-img'></span>
                                    <ul>
                                        <li>{user.phone}</li>
                                        <p>Phone</p>
                                    </ul>
                                </li>
                                <li>
                                    <span className='email-img'></span>
                                    <ul>
                                        <li>{user.email}</li>
                                        <p>Email</p>
                                    </ul>
                                </li>
                                <li>
                                    <span className='location-img'></span>
                                    <ul>
                                        <li>{user.address.street}</li>
                                        <li>{user.address.suit}</li>
                                        <li>{user.address.city}</li>
                                        <p>Work</p>
                                    </ul>
                                </li>
                                <li>
                                    <span className='social-img'></span>
                                    <ul>
                                        <li>{user.website}</li>
                                        <p>Social Media</p>
                                    </ul>
                                </li>
                                <li>
                                    <span className='company-img'></span>
                                    <ul>
                                        <li>{user.company.name}</li>
                                        <li>{user.company.catchPhrase}</li>
                                        <li>{user.company.bs}</li>
                                        <p>Company</p>
                                    </ul>
                                </li>
                            </ul>
                        ) : (
                            null
                        )}
                    </div>
                )
            })}
        </div>
    )
}

export default UserData;
