import { handleFetch } from '../../utils';
import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const UserData = () => {
    const [users, setUsers] = useState([]);
    const [toggleState, setToggleState] = useState(1);
    const [userTodo, setUserTodo] = useState([]);
    const [userPosts, setUserPosts] = useState([]);
    const [userAlbums, setUserAlbums] = useState([]);
    const {id} = useParams();
    const userId = +id;
    const navigate = useNavigate();

    useEffect(() => {
        const getUsers = async () => {
            setUsers(await handleFetch(`${process.env.REACT_APP_URL}users`));
        }

        const getUserAlbums = async () => {
            setUserAlbums(await handleFetch(`${process.env.REACT_APP_URL}users/${userId}/albums`));
        }

        const getUserTodo = async () => {
            setUserTodo(await handleFetch(`${process.env.REACT_APP_URL}users/${userId}/todos`));
        }

        const getUserPosts = async () => {
            setUserPosts(await handleFetch(`${process.env.REACT_APP_URL}users/${userId}/posts`));
        }

        getUsers();
        getUserAlbums();
        getUserTodo();
        getUserPosts();
    }, []);

    const handlePreviousRoute = () => {
        navigate('/users');
    }

    const toggleTab = (index) => {
        setToggleState(index);
    }

//     <table>
//     <thead>
//         <tr>
//             <th>Name</th>
//             <th>Username</th>
//             <th className='user-data-table-header'>User Data</th>
//         </tr>
//     </thead>
//     {users.map(user => {
//         return (
//             <tbody key={user.id}>
//                 <tr>
//                     <td>{user.name}</td>
//                     <td>{user.username}</td>
//                     <td className='user-contact'><Link to={`${user.id}`}></Link></td>
//                 </tr>    
//             </tbody>   
//         )
//     })}
// </table>

    return (
        <div className='user-page'>
            <button className='previous-page-button' onClick={() => handlePreviousRoute()}>Go Back</button>
            {users.map((user) => {
                return (
                    <div key={user.id}>
                        {user.id === userId ? (
                            <div>
                                <div className='tabs'>
                                    <div className='tabs-header'>
                                        <h4 onClick={() => toggleTab(1)}>albums</h4>
                                        <h4 onClick={() => toggleTab(2)}>todo</h4>
                                        <h4 onClick={() => toggleTab(3)}>posts</h4>
                                    </div>
                                    <div className='tabs-content'>
                                        <table className={toggleState === 1 ? 'tab-active' : 'tab-inactive'}>
                                            <thead>
                                                <tr>
                                                    <th>title</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                            {userAlbums.map((album) => {
                                                    return (
                                                        <tr>
                                                            <td>{album.title}</td>
                                                        </tr>
                                                    )
                                            })}
                                            </tbody>
                                        </table>
                                        <table className={toggleState === 2 ? 'tab-active' : 'tab-inactive'}>
                                            <thead>
                                                <tr>
                                                    <th>title</th>
                                                    <th>completed</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                            {userTodo.map((todo) => {
                                                return (
                                                    <div>
                                                        <p>{todo.title}</p>
                                                        <span 
                                                        className={todo.completed ? 'todo-completed' : 'todo-not-completed'}
                                                        ></span>
                                                    </div>
                                                )
                                            })}
                                            </tbody>
                                        </table>
                                        {/* <div key={user.id} className={toggleState === 2 ? 'tab-active' : 'tab-inactive'}>
                                            {userTodo.map((todo) => {
                                                return (
                                                    <div>
                                                        <p>{todo.title}</p>
                                                        <span 
                                                        className={todo.completed ? 'todo-completed' : 'todo-not-completed'}
                                                        ></span>
                                                    </div>
                                                )
                                            })}
                                        </div> */}
                                        <div className={toggleState === 3 ? 'tab-active' : 'tab-inactive'}>
                                            {userPosts.map((post) => {
                                                return (
                                                    <div>
                                                        <p>{post.title}</p>
                                                        <p>{post.body}</p>
                                                    </div>
                                                )
                                            })}
                                        </div>
                                    </div>
                                </div>
                                <ul className='user-info'>
                                    <li>
                                        <span className='mobile-img'></span>
                                        <ul>
                                            <li><a href={`tel:${user.phone}`}>{user.phone}</a></li>
                                            <li><p>Phone</p></li>
                                        </ul>
                                    </li>
                                    <li>
                                        <span className='email-img'></span>
                                        <ul>
                                            <li><a href={`mailto:${encodeURIComponent((user.email))}`}>{user.email}</a></li>
                                            <li><p>Email</p></li>
                                        </ul>
                                    </li>
                                    <li>
                                        <span className='location-img'></span>
                                        <ul>
                                            <li>{user.address.street}</li>
                                            <li>{user.address.suite}</li>
                                            <li>{user.address.city}</li>
                                            <li><p>Address</p></li>
                                        </ul>
                                    </li>
                                    <li>
                                        <span className='social-img'></span>
                                        <ul>
                                            <li>{user.website}</li>
                                            <li><p>Social Media</p></li>
                                        </ul>
                                    </li>
                                    <li>
                                        <span className='company-img'></span>
                                        <ul>
                                            <li>{user.company.name}</li>
                                            <li>{user.company.catchPhrase}</li>
                                            <li>{user.company.bs}</li>
                                            <li><p>Company</p></li>
                                        </ul>
                                    </li>
                                </ul>
                            </div>
                        ) : (
                            null
                        )}
                    </div>
                )
            })}
        </div>
    )
}

export default UserData;
