import { handleFetch } from '../../utils';
import { useEffect, useState } from 'react';
import { Link, Outlet, Routes, Route, useParams } from 'react-router-dom';

const UserData = () => {
    const [users, setUsers] = useState([]);
    const {id} = useParams();
    const userId = +id;

    useEffect(() => {
        const getUsers = async () => {
            setUsers(await handleFetch(`${process.env.REACT_APP_URL}users`));
        }

        getUsers();
    }, []);

    // const handleUserData = () => {

    //     const userContact = users.map(user => {
    //         if (user.id === userId) {
    //             return (
                    // <div key={user.id}>
                    //     <p>{user.phone}</p>
                    //     <p>{user.email}</p>
                    // </div>
    //             )
    //         }
    //     });

    //     return userContact;
    // }

    return (
        <div>
            <Outlet />
            {users.map((user) => {
                return (
                    <div key={user.id}>
                        {user.id === userId ? (
                            <div key={user.id}>
                                <p>{user.phone}</p>
                                <p>{user.email}</p>
                            </div>
                        ) : (
                            null
                        )}
                    </div>
                )
            })}
        </div>
    )
}

export default UserData;
