import { handleFetch } from "../../utils";
import { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";

function PostsList () {
    const [posts, setPosts] = useState([]);
    const { register, handleSubmit, reset, formState: { errors } } = useForm();
    const [isActive, setActive] = useState("false");

    useEffect(() => {
        const getPosts = async () => {
            const allPosts = await handleFetch(`${process.env.REACT_APP_URL}posts`);
            setPosts(allPosts.slice(0, 5));
        }

        getPosts();
    }, []);

    try {
        1 + b;
    } catch(err) {
        console.log(err);
        console.log('an error has occured');
    }

    const onSubmit = data => {
        setPosts( [...posts, data] );
        reset();
    }

    const FormTemplate = () => {
        const regEx = (/^([a-zA-Z0-9 _-]+)$/);
        const errorMessage = "This field is required and may contain letters and numbers only";

        return (
            <form onSubmit={handleSubmit(onSubmit)}>
                <label htmlFor="title-input">Title</label>
                <textarea className="title-input" id="title-input" rows={1} 
                {...register("title", {required: true, maxLength: 50, size:100, validate: (value) => regEx.test(value)})}/>
                {errors.title && <span className="error">{errorMessage}</span>}
                <label htmlFor="body-input">Body</label>
                <textarea className="body-input" id="body-input" rows={5} 
                {...register("body", {required: true, maxLength: 300, validate: (value) => regEx.test(value)})}/>
                {errors.body && <span className="error">{errorMessage}</span>}
                <input type="submit"/>
            </form>
        )
    }

    const toggleFormClass = () => {
        setActive(!isActive);
    }

    return (
        <div className="content">
            <button className="add-post-button" onClick={toggleFormClass}>{isActive ? "Add Post" : "Close Add Post"}</button>
            <div className={isActive ? "form-inactive" : "form-active"}>
                <FormTemplate />
            </div>
            {posts.length > 0 ? (
                <table className={isActive ? null : "table-darker"}>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Body</th>
                        </tr>
                    </thead>
                {posts.map((post, index) => {
                    return (
                        <tbody key={index + 1}>
                            <tr>
                                <td>{index + 1}</td>
                                <td>{post.title}</td>
                                <td>{post.body}</td>
                            </tr>
                        </tbody>
                    )
                })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default PostsList;
