import { useDispatch, useSelector } from "react-redux";
import { handleFetch } from "../../utils";
import { displayTodo, toggleTodo } from "../../store/actions/index";
import { useState, useEffect } from 'react';

function TodoList () {
    const dispatch = useDispatch();
    const todoSel = useSelector(state => state.todo.todo);
    const url = 'https://jsonplaceholder.typicode.com/todos';
    const [todo, setTodo] = useState([]);

    console.log(todoSel);

    // console.log(todoSel);

    useEffect(() => {
        dispatch(displayTodo(todo));
    }, []);

    useEffect(() => {
        const getTodo = async () => {
            setTodo(await handleFetch(url));
        }

        getTodo();
    }, []);

    const handleTodoToggle = async (id) => {
        const updatedTodos = todoSel.map(todo => {
            if (todo.id === id) {
                return {
                    ...todo,
                    completed: !todo.completed
                }
            }

            return todo;
        });

        // return {
        //     todo: updatedTodos
        // }
        setTodo(updatedTodos);
    }

    return (
        <div className="content">
            {todo.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Completed</th>
                        </tr>
                    </thead>
                   {todo.map((todo) => {
                        return (
                            <tbody key={todo.id}>
                                <tr>
                                    <td>{todo.id}</td>
                                    <td>{todo.title}</td>
                                    {todo.completed ?
                                        <td className="completed" role="button" 
                                        onClick={() => handleTodoToggle(todo.id)}></td>
                                    :
                                        <td className="not-completed" role="button" 
                                        onClick={() => handleTodoToggle(todo.id)}></td>
                                    }
                                </tr>
                            </tbody>
                        )
                    })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default TodoList;
