import { handleFetch } from '../../utils';
import { useEffect, useState } from 'react';
import { Link, Outlet, Routes, Route, useParams } from 'react-router-dom';

function UserList () {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const getUsers = async () => {
            setUsers(await handleFetch(`${process.env.REACT_APP_URL}users`));
        }

        getUsers();
    }, []);

    // const UserData = () => {
    //     const {id} = useParams();
    //     const userId = +id;

    //     const userContact = users.map(user => {
    //         if (user.id === userId) {
    //             return (
    //                 <div key={user.id}>
    //                     <p>{user.phone}</p>
    //                     <p>{user.email}</p>
    //                 </div>
    //             )
    //         }
    //     });

    //     return userContact;
    // }

    return (
        <div className='content'>
            {/* <Routes>
                <Route path=":id" element={<UserData />} />
            </Routes> */}
            {users.length ? (
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Username</th>
                            <th>User Data</th>
                        </tr>
                    </thead>
                    {users.map(user => {
                        return (
                            <tbody key={user.id}>
                                <tr>
                                    <td>{user.name}</td>
                                    <td>{user.username}</td>
                                    <td className='user-info'>
                                        <Link to={`${user.id}`}>
                                            <img src="./../../../../assets/img/info-green.png" alt="user-info-img" />
                                        </Link></td>
                                </tr>    
                            </tbody>   
                        )
                    })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default UserList;
