import { handleFetch } from "../../utils";
import { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";

function PostsList () {
    const [posts, setPosts] = useState([]);
    const [isActive, setActive] = useState("false");
    const { register, handleSubmit } = useForm();

    useEffect(() => {
        const getPosts = async () => {
            const allPosts = await handleFetch(`${process.env.REACT_APP_URL}posts`);
            setPosts(allPosts.slice(0, 5));
        }

        getPosts();
    }, []);

    const onSubmit = data => {
        setPosts( [...posts, data] );
    }

    const toggleClass = () => {
        setActive(!isActive);
    }

    return (
        <div className="content">
            <button className="add-post-button" onClick={toggleClass}>Add Post</button>
            <div className={isActive ? "form-inactive" : "form-active"}>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <input className="title-input" cols = "40" {...register("title", {required: true, maxLength: 50, size:100})}/>
                    <input className="body-input" {...register("body", {required: true, maxLength: 300})}/>
                    <input type="submit" />
                </form>
            </div>
            {posts.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Body</th>
                        </tr>
                    </thead>
                {posts.map((post, index) => {
                    return (
                        <tbody key={index + 1}>
                            <tr>
                                <td>{index + 1}</td>
                                <td>{post.title}</td>
                                <td>{post.body}</td>
                            </tr>
                        </tbody>
                    )
                })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default PostsList;
