import { useDispatch, useSelector } from "react-redux";
import { handleFetch } from "../../utils";
import { displayTodo } from "../../store/actions/index";
import { useState, useEffect } from 'react';

function TodoList () {
    const dispatch = useDispatch();
    const todoSel = useSelector(state => state.todo.todo);
    const url = 'https://jsonplaceholder.typicode.com/todos';
    const [todo, setTodo] = useState([]);

    useEffect(() => {
        const getTodo = async () => {
            setTodo(await handleFetch(url));
            dispatch(displayTodo(await handleFetch(url)));
        }

        getTodo();
    }, []);

    const handleTodoToggle = () => {
        const id = todoSel.map(res => res.id);
        const target = e.target.id
        console.log(id);
        // todoSel.map(todo => {

        // });
    }

    handleTodoToggle();

    return (
        <div className="content">
            {todo.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Completed</th>
                        </tr>
                    </thead>
                   {todo.map((todo) => {
                        return (
                            <tbody key={todo.id}>
                                <tr>
                                    <td>{todo.id}</td>
                                    <td>{todo.title}</td>
                                    {todo.completed ?
                                        <td className="completed" role="button"></td>
                                    :
                                        <td className="not-completed" role="button"></td>
                                    }
                                </tr>
                            </tbody>
                        )
                    })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default TodoList;
