import { handleFetch } from '../../utils';
import { useEffect, useState } from 'react';
import { Link, Outlet, Routes, Route, useParams } from 'react-router-dom';

const UserData = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const getUsers = async () => {
            setUsers(await handleFetch(`${process.env.REACT_APP_URL}users`));
        }

        getUsers();
        handleUserData()
    }, []);

    const handleUserData = () => {
        const {id} = useParams();
        const userId = +id;

        const userContact = users.map(user => {
            if (user.id === userId) {
                return (
                    <div key={user.id}>
                        <p>{user.phone}</p>
                        <p>{user.email}</p>
                    </div>
                )
            }
        });

        return userContact;
    }

    return (
        <div>
            <Outlet />
        </div>
    )
}

export default UserData;
